import { Component, OnDestroy } from '@angular/core';
import { AuthenticationService } from '../service/authentication.service';
import { User } from '../model/user';
import { Router } from '@angular/router';
import { HttpResponse } from '@angular/common/http';
import { ToastrService } from 'ngx-toastr';
import { Subscription } from 'rxjs';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.less']
})
export class RegisterComponent implements OnDestroy {
  public showLoading: boolean = false;
  private subscriptions: Subscription[] = [];

  constructor(
    private router: Router,
    private authService: AuthenticationService,
    private toastr: ToastrService
  ){}

  ngOnDestroy(): void {
    this.subscriptions.forEach(sub => {
      sub.unsubscribe();
    });
  }

  public onRegister(user: User): void {
    this.showLoading = true;
    const subscription = this.authService.register(user).subscribe(
      {
        next: (response: User) => {
          this.router.navigateByUrl('/login');
          this.toastr.info(`User "' + ${response.username} + '" registered`);
        },
        error: (errorResponse) => {
          this.toastr.error(errorResponse.error.message);
        }
      }
    );
    if(subscription !== null) {
      this.subscriptions.push(subscription);
    }
  }

}
<link href="https://maxcdn.bootstrapcdn.com/font-awesome/4.3.0/css/font-awesome.min.css" rel="stylesheet">
<br>
<br>
<div class="container">
  <div class="d-flex justify-content-center h-50">
    <div class="user_card">
      <div class="d-flex justify-content-center login-header-div">

      </div>

      <div class="d-flex justify-content-center form_container">
        <form #registerForm="ngForm" (ngSubmit)="onRegister(registerForm.value)">

          <div class="input-group mb-3">
            <div class="input-group-append">
              <span class="input-group-text"><i class="fas fa-user"></i></span>
            </div>
            <input type="text" class="form-control" name="username" placeholder="Username" required ngModel #usernameInput="ngModel">
          </div>
          <span class="help-block" *ngIf="usernameInput.invalid && usernameInput.touched">Please enter a username</span>

          <div class="input-group mb-2">
            <div class="input-group-append">
              <span class="input-group-text"><i class="fas fa-key"></i></span>
            </div>
            <input type="password" class="form-control" name="password" placeholder="Password" required ngModel #passwordInput="ngModel">
          </div>
          <span class="help-block" *ngIf="passwordInput.invalid && passwordInput.touched">Please enter a password</span>

          <div class="input-group mb-3">
            <div class="input-group-append">
              <span class="input-group-text"><i class="fas fa-user"></i></span>
            </div>
            <input type="text" class="form-control" name="firstName" placeholder="First Name" ngModel #firstNameInput="ngModel">
          </div>

          <div class="input-group mb-3">
            <div class="input-group-append">
              <span class="input-group-text"><i class="fas fa-user"></i></span>
            </div>
            <input type="text" class="form-control" name="lastName" placeholder="Last Name" ngModel #lastNameInput="ngModel">
          </div>

          <div class="input-group mb-3">
            <div class="input-group-append">
              <span class="input-group-text"><i class="fas fa-user"></i></span>
            </div>
            <input type="text" class="form-control" name="email" placeholder="example@mail.com" ngModel #emailInput="ngModel">
          </div>

          <div class="d-flex justify-content-center mt-3 login_container">
            <button type="submit" name="button" class="btn login_btn">
              <i class="fas fa-spinner fa-spin" *ngIf="showLoading"></i>&nbsp;&nbsp;
              <span>Register</span>
            </button>
          </div>
        </form>
      </div>

      <div class="mt-4">
        <div class="d-flex justify-content-center links">
          Already have an account?
          &nbsp;<a class="ml-2" style="color: darkslategray;" routerLink="/login">Log in</a>
        </div>
      </div>
    </div>
  </div>
</div>
